f:let main = (data, fields) => {
    //data是綁定數據源的接口數據，fields是圖表綁定了數據源中的哪些欄位
    //可以在綁定數據後，去掉下面的console.log前的註釋查看相應的數據
    //console.log(data, fields)
    //最後回傳圖表完整設定
    if(data && fields?.length) {//has data and bind fields
        let mermaidString = `
        timeline
    	title 测试时间线绑定数据
`;

        let ret = [];
        for (let d of data) {
            let single = [];
            for (let f of fields) {
                single.push(d[f.key]);
            }
            ret.push(single.join(' : '));
        }
        return mermaidString + `\r\n` + ret.join(`\r\n`);
    }
    return ``;//no bind info
};
